mapper.select:
SELECT
cp.*,CASE WHEN true THEN (st_distance (point (cp.position_x, cp.position_y),point(#{positionX,jdbcType=DECIMAL},#{positionY,jdbcType=DECIMAL}) ) * 111195) END distance
FROM
`cf_car_park` cp


    and cp.id = #{id,jdbcType=VARCHAR}


    and (st_distance (point (cp.position_x, cp.position_y),point(#{positionX,jdbcType=DECIMAL},#{positionY,jdbcType=DECIMAL}) ) * 111195)  <=  #{distance,jdbcType=INTEGER}


    and cp.name like "%"#{name,jdbcType=VARCHAR}"%"


    and cp.min_advance_subscribe_time  >=  #{minAdvanceSubscribeTime,jdbcType=BIGINT}


order by distance asc limit #{page,jdbcType=INTEGER},#{size,jdbcType=INTEGER}

select
sum(${countField})
from
cf_order


    and shop_id in

      #{item,jdbcType=VARCHAR}



    and shop_id=#{shopId,jdbcType=VARCHAR}


    and pay_time  >=  #{minPayTime,jdbcType=BIGINT}


    and pay_time  <  #{maxPayTime,jdbcType=BIGINT}



    and create_time  >=  #{minCreateTime,jdbcType=BIGINT}


    and create_time  <=  #{maxCreateTime,jdbcType=BIGINT}


    and update_time  >=  #{minUpdateTime,jdbcType=BIGINT}


    and update_time  <=  #{maxUpdateTime,jdbcType=BIGINT}



    and payment_agency_short_name = #{paymentAgencyShortName,jdbcType=VARCHAR}


    and goods_type = #{goodsType,jdbcType=TINYINT}
