mapper.select:
SELECT
cst.*,CASE WHEN true THEN (st_distance (point (cst.position_x, cst.position_y),point(#{positionX,jdbcType=DECIMAL},#{positionY,jdbcType=DECIMAL}) ) * 111195) END distance
FROM
`cf_charging_station` cst


    and (st_distance (point (cst.position_x, cst.position_y),point(#{positionX,jdbcType=DECIMAL},#{positionY,jdbcType=DECIMAL}) ) * 111195)  <=  #{distance,jdbcType=INTEGER}


    and cst.station_name like "%"#{stationName,jdbcType=VARCHAR}"%"


    and cst.station_type = #{stationType,jdbcType=TINYINT}


order by distance asc limit #{page,jdbcType=INTEGER},#{size,jdbcType=INTEGER}
