select lgshtslg.*,lgsh.storehouse_name lgsh_storehouse_name,lgsh.position_x lgsh_position_x,lgsh.position_y lgsh_position_y,lgsh.address lgsh_address,lgsh.phone lgsh_phone,lgsh.image lgsh_image,usr.user_name usr_user_name,usr.nick_name usr_nick_name,
usr.true_name usr_true_name,usr.phone usr_phone,usr.avatar usr_avatar,lgtsk.id lgtsk_id,lgtsk.pid lgtsk_pid,lgtsk.editer_uid lgtsk_editer_uid,lgtsk.create_time lgtsk_create_time,lgtsk.start_time lgtsk_start_time,
lgtsk.end_time lgtsk_end_time,lgtsk.update_time lgtsk_update_time,lgtsk.origin lgtsk_origin,lgtsk.destination lgtsk_destination,lgtsk.cargo_type lgtsk_cargo_type,lgtsk.car_type lgtsk_car_type,
lgtsk.vehicles_required lgtsk_vehicles_required,lgtsk.reserved_vehicles lgtsk_reserved_vehicles,lgtsk.res_each lgtsk_res_each,lgtsk.weight lgtsk_weight,lgtsk.logistics_storehouse_id lgtsk_logistics_storehouse_id,lgtsk.task_status lgtsk_task_status,
lgtsk.task_type lgtsk_task_type,lgtsk.factory_id lgtsk_factory_id,lgtsk.supplier lgtsk_supplier,lgtsk.waybill_number lgtsk_waybill_number,lgtsk.task_title lgtsk_task_title,lgtsk.emergency_level lgtsk_emergency_level,
lqa.area_name lqa_area_name,lgft.factory_name lgft_factory_name
from cf_logistics_task_log lgshtslg
left join cf_logistics_storehouse lgsh on (lgshtslg.logistics_storehouse_id=lgsh.id)
left join cf_logistics_queuing_area lqa on (lgshtslg.queuing_area_id=lqa.id)
left join cf_logistics_task lgtsk on (lgshtslg.task_id=lgtsk.id)
left join cf_user usr on(lgshtslg.deliver_uid=usr.id)
left join cf_logistics_factory lgft on(lgshtslg.factory_id=lgft.id)


    and lgshtslg.id = #{id,jdbcType=BIGINT}


    and lgshtslg.task_id = #{taskId,jdbcType=BIGINT}


    and lgshtslg.pid = #{pid,jdbcType=BIGINT}


    and lgshtslg.deliver_uid = #{deliverUid,jdbcType=VARCHAR}


    and lgshtslg.checker_uid = #{checkerUid,jdbcType=VARCHAR}


    and lgshtslg.task_log_status = #{taskLogStatus,jdbcType=TINYINT}


    and lgshtslg.number_plate = #{numberPlate,jdbcType=VARCHAR}


    and lgshtslg.delivery_note_code = #{deliveryNoteCode,jdbcType=VARCHAR}


    and lgshtslg.item_number = #{itemNumber,jdbcType=VARCHAR}


    and lgshtslg.category = #{category,jdbcType=VARCHAR}


    and lgshtslg.logistics_storehouse_id = #{logisticsStorehouseId,jdbcType=BIGINT}


    and lgshtslg.queuing_area_id = #{queuingAreaId,jdbcType=BIGINT}


    and lgshtslg.logistics_storehouse_id in

      #{item,jdbcType=BIGINT}



    and lgshtslg.logistics_platform_id = #{logisticsPlatformId,jdbcType=BIGINT}


    and lgshtslg.notify_driver_time = #{notifyDriverTime,jdbcType=BIGINT}


    and lgshtslg.notify_driver_time  >=  #{minNotifyDriverTime,jdbcType=BIGINT}


    and lgshtslg.notify_driver_time  <=  #{maxNotifyDriverTime,jdbcType=BIGINT}


    and lgshtslg.create_time  >=  #{minCreateTime,jdbcType=BIGINT}


    and lgshtslg.create_time  <=  #{maxCreateTime,jdbcType=BIGINT}


    and lgshtslg.task_log_status  >=  #{minTaskLogStatus,jdbcType=TINYINT}


    and lgshtslg.task_log_status  <=  #{maxTaskLogStatus,jdbcType=TINYINT}



  order by ${orderBy}


  limit ${(page-1)*size},${size}

