mapper.select:
SELECT
(from_unixtime(
create_time / 1000,
'%Y-%m-%d'
)) date_str,

  sum(1) counts


  sum(amount_actually_paid) counts

FROM
cf_order
WHERE
create_time  >=  unix_timestamp(
DATE_FORMAT(NOW(), '%Y-%m-%d 00:00:00') - INTERVAL #{day,jdbcType=INTEGER} DAY
) * 1000 and create_time  <  unix_timestamp(
DATE_FORMAT(NOW(), '%Y-%m-%d 00:00:00')
) * 1000
and goods_type=#{goodsType,jdbcType=TINYINT}
and status=#{status,jdbcType=TINYINT}
and pay_time  >=  #{payTime,jdbcType=BIGINT}
and refund_time  >=  #{refundTime,jdbcType=BIGINT}
and cancel_time  >=  #{cancelTime,jdbcType=BIGINT}
and payment_agency_short_name=#{paymentAgencyShortName,jdbcType=VARCHAR}

  GROUP BY date_str

SELECT

  (from_unixtime(
  pay_time / 1000,
  '%Y-%m-%d'
  )) date_str,


  sum(1) counts


  sum(amount_actually_paid) counts


  sum(amounts_payable) counts

FROM
cf_order
WHERE
pay_time  >=  unix_timestamp(#{date,jdbcType=VARCHAR})*1000 and pay_time  <=  unix_timestamp(#{endDate,jdbcType=VARCHAR})*1000
and goods_type=#{goodsType,jdbcType=TINYINT}
and status=#{status,jdbcType=TINYINT}
and pay_time  >=  #{payTime,jdbcType=BIGINT}
and refund_time  >=  #{refundTime,jdbcType=BIGINT}
and cancel_time  >=  #{cancelTime,jdbcType=BIGINT}
and payment_agency_short_name=#{paymentAgencyShortName,jdbcType=VARCHAR}
and shop_id=#{shopId,jdbcType=VARCHAR}

  and shop_id in

    #{item,jdbcType=VARCHAR}


and payment_agency_short_name in

    #{item,jdbcType=VARCHAR}



  GROUP BY date_str

SELECT
( from_unixtime( create_time / 1000, '%Y-%m-%d' ) ) date_str,

  sum(1) counts


  sum(amount_actually_paid) counts

FROM
cf_order
WHERE
create_time  >=  unix_timestamp( DATE_ADD(DATE_FORMAT(#{date,jdbcType=VARCHAR},'%Y-%m-%d %H:%i:%S'),interval -day(DATE_FORMAT(#{date,jdbcType=VARCHAR},'%Y-%m-%d %H:%i:%S'))+1 day)
) * 1000
AND create_time  <  unix_timestamp( DATE_FORMAT(last_day(DATE_FORMAT(#{date,jdbcType=VARCHAR},'%Y-%m-%d %H:%i:%S')),'%Y-%m-%d %H:%i:%S') + INTERVAL 1 DAY ) * 1000
and goods_type=#{goodsType,jdbcType=TINYINT}
and status=#{status,jdbcType=TINYINT}
and pay_time  >=  #{payTime,jdbcType=BIGINT}
and refund_time  >=  #{refundTime,jdbcType=BIGINT}
and cancel_time  >=  #{cancelTime,jdbcType=BIGINT}
and payment_agency_short_name=#{paymentAgencyShortName,jdbcType=VARCHAR}

  GROUP BY date_str

SELECT
(from_unixtime(
create_time / 1000,
'%Y-%m'
)) date_str,

  sum(1) counts


  sum(amount_actually_paid) counts

FROM
cf_order
WHERE
create_time  >=  unix_timestamp(DATE_FORMAT(#{date,jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%S')) * 1000
AND create_time  <=  unix_timestamp(DATE_FORMAT(#{endDate,jdbcType=VARCHAR}, '%Y-%m-%d %H:%i:%S')) * 1000
and goods_type=#{goodsType,jdbcType=TINYINT}
and status=#{status,jdbcType=TINYINT}
and pay_time  >=  #{payTime,jdbcType=BIGINT}
and refund_time  >=  #{refundTime,jdbcType=BIGINT}
and cancel_time  >=  #{cancelTime,jdbcType=BIGINT}
and payment_agency_short_name=#{paymentAgencyShortName,jdbcType=VARCHAR}

  GROUP BY date_str

select sum(${countField}) from cf_order


    and goods_type = #{goodsType,jdbcType=TINYINT}


    and shop_id = #{shopId,jdbcType=VARCHAR}


    and status = #{status,jdbcType=TINYINT}


    and create_time  >=  #{minCreateTime,jdbcType=BIGINT}


    and create_time  <=  #{maxCreateTime,jdbcType=BIGINT}


    and pay_time = #{payTime,jdbcType=BIGINT}


    and pay_time  >=  #{minPayTime,jdbcType=BIGINT}


    and pay_time  <=  #{maxPayTime,jdbcType=BIGINT}


    and payment_agency_short_name = #{paymentAgencyShortName,jdbcType=VARCHAR}
